void main() { gl_Position = vert(); }

glsl1.frag:
precision mediump float;
uniform highp vec4 cc_time;
vec4 CCFragOutput (vec4 color) {
  return color;
}
varying vec2 uv0;
uniform sampler2D cc_spriteTexture;
vec4 frag () {
  vec2 uv = uv0.xy;
  uv.x -= cc_time.x * 0.5;
  uv.x=fract(uv.x);
  vec4 color = texture2D(cc_spriteTexture, uv);
  return CCFragOutput(color);
}
void main() { gl_FragColor = frag(); }
